#include <iostream>
#include <algorithm>

using namespace std;
#define SIZE    10 + 1

int arr[SIZE];
int sorted[SIZE];

int dnc(int left, int right) {
    // return solution of arr[left ~ right]
    // divide & conquer
    if(left >= right) return 0;
    
    int mid = (left+right)/2;
    int ret = dnc(left, mid) + dnc(mid+1, right);
    int i, j = mid+1;
    
    // count inversions generated by (arr[i], arr[j])
    // with arr[i] in left array & arr[j] in right array
    // O(N)
    for(i = left; i <= mid; i++) {
        while(arr[i] > arr[j] && j <= right) j++;
        j--; // j : last idx with arr[i] > arr[j]
        ret += j-mid;
    }
    
    // merge left & right arrays
    i = left; j = mid+1;
    int k = left;
    while(i <= mid && j <= right) {
        if(arr[i] <= arr[j])
            sorted[k++] = arr[i++];
        else
            sorted[k++] = arr[j++];
    }
    if(j > right) {
        while(i <= mid)
            sorted[k++] = arr[i++];
    }
    else {
        while(j <= right)
            sorted[k++] = arr[j++];
    }
    
    // copy sorted[left~right] to arr[left~right]
    for(k = left; k <= right; k++) {
        arr[k] = sorted[k];
    }
    
    return ret;
}

int main( ) {
    ios_base::sync_with_stdio(false); cin.tie(NULL);
    freopen("input.txt", "r", stdin);
    
    int n;
    cin >> n;
    for(int i = 0; i < n; i++) {
        cin >> arr[i];
    }
    
    
    return 0;
}
